import { FacadeProxyHandler } from '#src/facades/FacadeProxyHandler';
export class Facade {
    /**
     * Create a new Facade for the alias specified.
     */
    static createFor(alias) {
        return new Proxy(this, new FacadeProxyHandler(alias));
    }
}
